
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=3;
		switch (day)
		{
		case 1: System.out.println("1"); break;
		case 2: System.out.println("2"); break;
		case 3: System.out.println("3"); break;
		case 4: System.out.println("4"); break;
		}
		
	}

}
